		private static void ConnectionSpecificParameterMagic(UnitOfWorkContext unitOfWorkContext, IDataParameter commandParameter, string generatedFromColumnNativeType)
		{
			if ((object)unitOfWorkContext == null)
				throw new ArgumentNullException("unitOfWorkContext");

			if ((object)commandParameter == null)
				throw new ArgumentNullException("commandParameter");

			switch (DataSourceTag.SafeToString().ToLower())
			{
				case "net.sqlserver":
					NetSqlServerSpecificParameterMagic(unitOfWorkContext, commandParameter, generatedFromColumnNativeType);
					break;
				case "net.sqlite":
					NetSqliteSpecificParameterMagic(unitOfWorkContext, commandParameter, generatedFromColumnNativeType);
					break;
				case "odbc.sqlserver":
					OdbcSqlServerSpecificParameterMagic(unitOfWorkContext, commandParameter, generatedFromColumnNativeType);
					break;
				case "odbc.sybaseiq":
					OdbcSybaseIqSpecificParameterMagic(unitOfWorkContext, commandParameter, generatedFromColumnNativeType);
					break;
				default:
					throw new InvalidOperationException("TODO (enhancement): add meaningful message");
			}
		}

		private static void ConnectionSpecificCommandMagic(UnitOfWorkContext unitOfWorkContext, bool executeAsCud, out int thisOrThatRecordsAffected)
		{
			if ((object)unitOfWorkContext == null)
				throw new ArgumentNullException("unitOfWorkContext");
			
			if (executeAsCud)
			{
				switch (DataSourceTag.SafeToString().ToLower())
				{
					case "net.sqlserver":
						thisOrThatRecordsAffected = NET_SQL_SERVER_PERSIST_NOT_EXPECTED_RECORDS_AFFECTED;
						break;
					case "net.sqlite":
						thisOrThatRecordsAffected = NET_SQLITE_PERSIST_NOT_EXPECTED_RECORDS_AFFECTED;
						break;
					case "odbc.sqlserver":
						thisOrThatRecordsAffected = ODBC_SQL_SERVER_PERSIST_NOT_EXPECTED_RECORDS_AFFECTED;
						break;
					case "odbc.sybaseiq":
						thisOrThatRecordsAffected = ODBC_SYBASE_IQ_PERSIST_NOT_EXPECTED_RECORDS_AFFECTED;
						break;
					default:
						throw new InvalidOperationException("TODO (enhancement): add meaningful message"); 
				}
			}
			else
			{
				switch (DataSourceTag.SafeToString().ToLower())
				{
					case "net.sqlserver":
						thisOrThatRecordsAffected = NET_SQL_SERVER_QUERY_EXPECTED_RECORDS_AFFECTED;
						break;
					case "net.sqlite":
						thisOrThatRecordsAffected = NET_SQLITE_QUERY_EXPECTED_RECORDS_AFFECTED;
						break;
					case "odbc.sqlserver":
						thisOrThatRecordsAffected = ODBC_SQL_SERVER_QUERY_EXPECTED_RECORDS_AFFECTED;
						break;
					case "odbc.sybaseiq":
						thisOrThatRecordsAffected = ODBC_SYBASE_IQ_QUERY_EXPECTED_RECORDS_AFFECTED;
						break;
					default:
						throw new InvalidOperationException("TODO (enhancement): add meaningful message"); 
				}
			}
		}
		
